sys_variable_value.value:
// Set the variable 'answer' to true or false to indicate if the condition has been met or not.
answer = intCheckScript();

function intCheckScript() {
	var intSched = new GlideRecord('x_4s_recruiting_candidate_interviews');
	intSched.addQuery('open_position', current.getUniqueValue());
	intSched.addActiveQuery();
	intSched.query();
	if (intSched.next()) {
		return true;
	}
	return false;
}

