sys_variable_value.value:
var newCand = new GlideRecord('x_4s_recruiting_candidate_interviews');
newCand.initialize();
newCand.candidate = current.sys_id;
newCand.open_position = current.open_position;
newCand.interviewer = current.open_position_applied_for.hiring_manager;
newCand.setValue('interview_type', 'hm_interview');
var targetID = newCand.insert();
var sourceID = current.sys_id;
var sourceTable = current.sys_class_name;
var targetTable = 'x_4s_recruiting_candidate_interviews';

var attachment = new GlideRecord('sys_attachment');    
attachment.addQuery('table_name', sourceTable);  
attachment.addQuery('table_sys_id',sourceID);    
attachment.query();  

if (attachment.next())
	copyAttachments(sourceTable,sourceID, targetTable, targetID);

current.hiring_manager_interview.sys_id = targetID;
current.update();



function copyAttachments(sourceTable, sourceID, targetTable, targetID) {  
    var att = new GlideRecord("sys_attachment");  
    att.addQuery("table_name", sourceTable);  
    att.addQuery("table_sys_id", sourceID);  
    att.query();  
    gs.info(att.getEncodedQuery());  
    gs.info("copying " + att.getRowCount());  
  
    while (att.next()) {  
        //copy attachment record  
        var oldSysID = att.getUniqueValue();  
        var targetName = targetTable;  
        var sourceName = att.getValue("table_name");  
     
        att.setValue("table_name", targetName);  
        att.setValue("table_sys_id", targetID);  
        var newSysID = att.insert();  
     
        //copy attachment doc records  
        var doc = new GlideRecord("sys_attachment_doc");  
        doc.addQuery("sys_attachment", oldSysID);  
        doc.query();  
        while (doc.next()) {  
            doc.setValue("sys_attachment", newSysID);  
            doc.insert();  
        }  
    }  
}

