answer = checkComplete();

function checkComplete(){
	var recCk = new GlideRecord('x_4s_recruiting_candidate_interviews');
	recCk.get(current.hiring_manager_interview);
	if (recCk.status == 2 || recCk.status == 3){
		return true;
	}
	return false;
}

